import React, { useEffect, useState } from "react";
import axios from "axios";

function CreateDonation() {
  const [errors, setErrors] = useState({});
  const [submitted, setSubmitted] = useState(false);
  const [userForm, setUserForm] = useState({


    //Field names in the pet donation form

first_name: "",
last_name: "",
contact_no: "",
email: "",
amount: "",
payment_mode: "",
address: "",


  });

  const inputsHandler = (e) => {
    const { name, value } = e.target;

    setUserForm({
      ...userForm,
      [name]: value
    });
  };

  const validateForm = () => {
    let isValid = true;
    const newErrors = {};

    // Validate email
    if (!userForm.email) {
      newErrors.email = "Email is required";
      isValid = false;
    }


    if (!userForm.name) {
      newErrors.name = "Name is required";
      isValid = false;
    }


    if (!userForm.rollno) {
      newErrors.rollno = "Email is required";
      isValid = false;
    }



       setErrors(newErrors);
    return isValid;
  };

  const onSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:4000/donations/create-donation", userForm)
      .then((res) => {
        console.log(res.data);
        setUserForm({


        first_name: "",
        last_name: "",
        contact_no: "",
        email: "",
        amount: "",
        payment_mode: "",
        address: "",

        });
      });

      if (validateForm()) {
        // Form is valid, you can submit or process the data here



        console.log("Form data:", userForm);
        setSubmitted(true); // Set a submitted flag
      } else {
        // Form is not valid, display error messages
      }
  };

  useEffect(() => {}, []);

  const isFormValid = Object.keys(errors).length === 0;
  return (
    <div>
      <div className="form-wrapper">

      <h2>Login</h2>
      {submitted ? (
        <div className="success-message">Login successful!</div>
      ) : (
        <form onSubmit={onSubmit}>


          <div className="mb-3">
            <label className="form-label">First Name:</label>
            <input
              type="text"
              className="form-control"
              name="first_name"
              id="first_name"
              value={userForm.first_name}
              onChange={inputsHandler}
            />
          </div>


          <div className="mb-3">
            <label className="form-label">Last Name:</label>
            <input
              type="text"
              className="form-control"
              name="last_name"
              id="last_name"
              value={userForm.last_name}
              onChange={inputsHandler}
            />
          </div>


          <div className="mb-3">
            <label className="form-label">Contact No:</label>
            <input
              type="text"
              className="form-control"
              name="contact_no"
              id="contact_no"
              value={userForm.contact_no}
              onChange={inputsHandler}
            />
          </div>


          <div className="mb-3">
            <label className="form-label">Email:</label>
            <input
              type="text"
              className="form-control"
              name="email"
              id="email"
              value={userForm.email}
              onChange={inputsHandler}
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Amount:</label>
            <input
              type="text"
              className="form-control"
              name="amount"
              id="amount"
              value={userForm.amount}
              onChange={inputsHandler}
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Payment Mode:</label>
            <input
              type="text"
              className="form-control"
              name="payment_mode"
              id="payment_mode"
              value={userForm.payment_mode}
              onChange={inputsHandler}
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Address:</label>
            <input
              type="text"
              className="form-control"
              name="address"
              id="address"
              value={userForm.address}
              onChange={inputsHandler}
            />
          </div>

          <div className="mb-3">
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </div>
        </form>
    )}
      </div>
    </div>
  );
}

export default CreateDonation;
